(499, 115, '_themeisle_gutenberg_block_styles', '.jobseeker-form{width:100%;max-width:600px;margin:30px auto;padding:30px;background-color:#f9f9f9;border:1px solid #e0e0e0;border-radius:8px;box-shadow:0 4px 8px rgba(0,0,0,.1)}.jobseeker-form input[type=\"text\"], \n    .jobseeker-form input[type=\"email\"], \n    .jobseeker-form input[type=\"password\"], \n    .jobseeker-form input[type=\"tel\"], \n    .jobseeker-form textarea {\n        width: 100%;\n        padding: 12px;\n        margin: 12px 0;\n        border: 1px solid #b2c8d4; /* Light blue-gray border */\n        border-radius: 6px;\n        font-size: 16px;\n        background-color: #f0f8ff; /* Soft light blue background */\n        color: #333;\n        transition: all 0.3s ease-in-out;\n    }\n\n    /* Focus effect for input fields inside the form */\n    .jobseeker-form input[type=\"text\"]:focus,\n    .jobseeker-form input[type=\"email\"]:focus,\n    .jobseeker-form input[type=\"password\"]:focus,\n    .jobseeker-form input[type=\"tel\"]:focus,\n    .jobseeker-form textarea:focus {\n        border-color: #6cb2eb; /* Blue border on focus */\n        background-color: #e6f7ff; /* Light blue on focus */\n    }\n\n    /* Label styling */\n    .jobseeker-form label {\n        font-size: 16px;\n        color: #007bff; /* Beautiful blue color for labels */\n        font-weight: bold;\n        margin-bottom: 5px;\n        display: block;\n    }\n\n    /* Submit button styling */\n    .jobseeker-form input[type=\"submit\"] {\n        background-color: #007bff; /* Blue background for the button */\n        color: white;\n        padding: 14px 24px;\n        border: none;\n        border-radius: 6px;\n        font-size: 18px;\n        cursor: pointer;\n        transition: all 0.3s ease-in-out;\n    }\n\n    .jobseeker-form input[type=\"submit\"]:hover {\n        background-color: #0056b3; /* Darker blue on hover */\n    }\n\n    .jobseeker-form input[type=\"submit\"]:active {\n        background-color: #003f7f; /* Even darker blue when clicked */\n    }\n\n    /* Styling for the form container */\n    .jobseeker-form .form-container {\n        font-family: Arial, sans-serif;\n        background-color: #f5f5f5; /* Beige background around the form */\n        padding: 40px;\n        border-radius: 12px;\n        box-shadow: 0 6px 12px rgba(0, 0, 0, 0.1); /* Soft shadow */\n    }\n\n    /* Heading Styling */\n    .jobseeker-form h2 {\n        font-size: 28px;\n        color: #333;\n        text-align: center;\n        margin-bottom: 30px;\n        color: #003366; /* Deep blue color for the heading */\n    }\n\n    /* Textarea specific styles */\n    .jobseeker-form textarea {\n        resize: vertical; /* Allow the user to resize the textarea vertically */\n        height: 150px; /* Initial height */\n    }\n\n    /* Margin for input and textarea elements */\n    .jobseeker-form input[type=\"text\"], \n    .jobseeker-form input[type=\"email\"], \n    .jobseeker-form input[type=\"password\"], \n    .jobseeker-form input[type=\"tel\"],.jobseeker-form textarea{margin-bottom:15px}'),
